import AdminTab from "../components/adminTab"
import { getAllOrder } from '../api/adminApi'
import { useEffect, useState } from "react"

const AdminOrder = () => {
    const [orders, setOrders] = useState([])

    useEffect(() => {
        (async () => {
            const token = localStorage.getItem('user')
            const res = await getAllOrder(token)
            if (res) setOrders(res)
        })()
    },[])

    return (
        <div className="flex flex-row">
            <AdminTab />

            <div className="bg-primary_color/10 h-screen w-[85%] px-20 py-8">
                <div className="bg-white drop-shadow-lg rounded p-8">
                {
                    orders.map((order)=>(
                        <li key={order.orderID} className="grid grid-cols-4 space-y-8 text-sm items-center mx-auto text-gray-500 font-semibold">
                            <p className="px-4 font-semibold">OrderID: {order.orderID}</p>
                            <p className="px-4 text-gray-600">UserID: {order.userID}</p>
                            <p className="px-4 text-blue-600">{order.totalPrice} VND</p>
                            <p className="md:text-base text-gray-800 flex items-center gap-2">Payment method: {order.paymentMethod}</p>
                        </li>
                    ))
                }
                </div>
            </div>
        </div>
    )
}

export default AdminOrder
