import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore, { Pagination, Autoplay } from "swiper";
import { NavLink } from "react-router-dom";

import 'swiper/css';
import 'swiper/css/pagination';

import logo from '../img/logo.png'
import slider1 from '../img/slider1.jpg'
import slider2 from '../img/slider2.jpg'
import slider3 from '../img/slider3.jpg'

const NavBarList = [
    {
        name: 'Trang chủ',
        path: '/'
    },
    {
        name: 'Thú cưng',
        path: '/pet'
    },
    {
        name: 'Thức ăn',
        path: '/petFood'
    },
    {
        name: 'Phụ kiện',
        path: '/petProduct'
    },
    {
        name: 'Dịch vụ',
        path: '/petService'
    },
    {
        name: 'Giới thiệu',
        path: '/About'
    },
    {
        name: 'Liên hệ',
        path: '/Contact'
    }
]

const Header = () => {
    SwiperCore.use([Autoplay])

    return (
        <div class="flex flex-row mr-48 ml-16">
            <div class="w-1/3 flex flex-col items-center mt-2">
                <NavLink 
                    to='/signIn'
                    className={({isActive}) => 
                        isActive
                        ? 'font-semibold text-sm tracking-tighter px-3 py-2 rounded-md bg-primary_color text-white'
                        : 'font-semibold text-sm tracking-tighter px-3 py-2 rounded-md hover:text-primary_color duration-300 border-2'
                    }
                >
                    ĐĂNG NHẬP
                </NavLink>
                <img src={logo} alt='logo'></img>
            </div>
            <div class="w-2/3">
                {/* tab */}
                <div className='flex flex-row my-4'>
                    {NavBarList.map((item) => (
                        <div key={item.name}>
                            <NavLink 
                                to={item.path}
                                className={({isActive}) => 
                                    isActive
                                    ? 'font-semibold text-sm tracking-tighter px-3 py-2 rounded-md bg-primary_color text-white'
                                    : 'font-semibold text-sm tracking-tighter px-3 py-2 rounded-md hover:text-primary_color duration-300'
                                }
                            >
                                {item.name.toUpperCase()}
                            </NavLink>
                        </div>
                    ))}
                    <div> 
                        <NavLink 
                            to='/order'
                            className={({isActive}) => 
                                isActive
                                ? 'font-semibold text-sm tracking-tighter px-3 py-2 rounded-md bg-primary_color text-white'
                                : 'font-semibold text-sm tracking-tighter px-3 py-2 rounded-md hover:text-primary_color duration-300'
                            }
                        >
                            GIỎ HÀNG
                        </NavLink>
                    </div>
                </div>

                {/* swipper */}
                <div className='mt-16 mb-6'>
                    <Swiper
                        pagination={{ dynamicBullets: true }}
                        modules={[Pagination]}
                        autoplay={{ delay: 2000 }}
                    >
                        <SwiperSlide><img src={slider1} alt='slider1'></img></SwiperSlide>
                        <SwiperSlide><img src={slider2} alt='slider2'></img></SwiperSlide>
                        <SwiperSlide><img src={slider3} alt='slider3'></img></SwiperSlide>
                    </Swiper>
                </div>

                {/* search */}
                <div className='flex flex-row-reverse items-center'>
                    <div className='bg-[#333] py-3 px-3 border-2 border-[#333] rounded'>
                        <svg style={{color: 'white'}} xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-search" viewBox="0 0 16 16"> <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z" fill="white"></path> </svg>
                    </div>
                    <input type="text" name='search' placeholder='Tìm kiếm...' className='border-t-2 border-b-2 border-l-2 rounded-l py-2 px-3 w-1/2'/>
                </div>
            </div>
        </div>
    )
}

export default Header
